INSERT INTO `flr_description` (`id`, `id_plant`, `isNew`, `description`, `sales_terms`, `main_pic`, `other_pic`, `presence`) VALUES
(1, 1, 'false', 'Форма роста лиана. Листья перистые,около 15см, но могу достигать и более крупных размеров. В уходе не сложен. Предпочитает повышенную влажность воздуха, северное или восточное окно.  Не прихотливый в уходе.', ' укоренённый черенок', 'anthurium_pentaphyllum.jpg', '', 'false'),
(2, 2, 'false', 'Форма роста лиана. Темп роста средний. Довольно компактное растение с некрупными, около 10-15 см, ажурными листьями. В уходе не сложен. Предпочитает повышенную влажность воздуха, северное или восточное окно. ', 'временно нет', 'anthurium_polystichum.jpg', '', 'true'),
(3, 3, 'false', 'Форма роста лиана. Листья довольно крупные, около 25см, но могу достигать и более крупных размеров. В уходе не сложен. Предпочитает повышенную влажность воздуха, северное или восточное окно. ', ' укоренённый черенок', 'anthurium_variabile.jpg', '', 'false'),
(4, 4, 'false', 'Форма роста лиана/ампель.Темп роста довольно быстрый. Листья около 5см, гафрированные, тёмно-зелёные с большим колисеством белых и кремово-зелёных пятен. Нуждается в притенении.', ' укоренённый черенок', 'epipr_n_joy.jpg', '', 'false'),
(5, 5, 'true', 'новинка! Форма роста лиана. Темп роста выше среднего. Очень интересный сорт. Листья крупные, многорассеченные. Междоузлия длинные. Растение нуждается в опоре.Нуждается в притенении.', ' укоренённый черенок', 'epipr_fish_bone.jpg', '', 'false'),
(6, 6, 'true', 'новинка!', ' укоренённый черенок', 'epipr_ex_aureum_1.jpg', '', 'false'),
(7, 7, 'false', 'Форма роста лиана. Растение нуждается  опоре. Темп роста выше среднего. Листья 12-15см, за счёт частых и глубоких вырезов, кажутся ажурными. Полив умеренный, освещение без прямого солнца. Не прихотливый в уходе.', ' укоренённый черенок', 'epipr_skeleton.jpg', '', 'false'),
(8, 8, 'false', 'Форма роста лиана. Темп роста средний. Листья довольно крупные, тёмно-зелёные, бархатистые. Растение нуждается в опоре. Теневынослиивый сорт.', 'временно нет', 'epipr_sp.jpg', '', 'true'),
(9, 9, 'true', 'новинка!', ' укоренённый черенок', 'epipremnum_sp_var.jpg', '', 'false'),
(10, 10, 'true', 'новинка! Форма роста лиана. Темп роста медленный. Листья продолговатые, глянцевые, тёмно-изумрудного цвета. Междоузлия и черешки короткие. Теневыносливый сорт. Не прихотливый в уходе.', ' укоренённый черенок', 'epipr_blue_velvet.jpg', '', 'false'),
(11, 11, 'true', 'новинка! Форма роста лиана. Листовая пластинка покрыта широкими пятнами и дилнными мазками серебристого цвета.Расположение у западных и восточных окон,содержать при 20-25°С,полив круглый год умеренный.', ' укоренённый черенок', 'epipr_exotica.jpg', '', 'false'),
(12, 12, 'false', 'Форма роста - дерево/куст. Листья вытянутые, длиной около 10 см, окрашены в зелёный с салатовыми мазками. Темп роста средний. Теневынослив.', 'укоренённый черенок около 15см', 'ficus_amstel_green_gold.jpg', '', 'false'),
(13, 13, 'false', 'Форма роста - дерево/куст. Листья около 3,5 см. имеют причудливую форму. Темп роста ниже среднего. Светолюбив.', 'укоренённый черенок около 15см', 'ficus_irene.jpg', '', 'false'),
(14, 14, 'false', 'Форма роста - дерево/куст. Мелколистный сорт.Листья светло-зелёные с кремово-белой окантовкой. Лист немного сложен вдоль центральной жилки. Темп роста средний', 'укоренённый черенок около 15см', 'natasja_var.jpg', '', 'false'),
(15, 15, 'false', 'Форма роста - дерево/куст. Листья тёмно-зелёные, волнистые, покрыты белыми пятнами разного размера. Часть листьев может быть полностью белой. Темп роста средний. Теневынослив. ', 'укоренённый черенок около 15см', 'ficus_curly.jpg', '', 'false'),
(16, 16, 'false', 'Форма роста - дерево/куст. Листовая пластина среднего размера, окрашена в тёмно зелёный цвет. По центральной жилке ярко-салатовые мазки.', 'укоренённый черенок около 15см', 'ficus_denylight.jpg', '', 'false'),
(17, 17, 'false', 'Форма роста - дерево/куст. Листья около 7 см. Листовая пластина тёмно-зелёная, по краю светло-травянистый кант. Центральная жилка салатовая. Темп роста быстрый.', 'укоренённый черенок около 15см', 'golden_king.jpg', '', 'false'),
(18, 18, 'false', 'Форма роста - дерево/куст. Листовая пластина среднего размера, окрашена в ярко салатовый цвет. По центральной жилке вкрапления тёмнозелёного цвета. Край листа очень волнистый.', 'укоренённый черенок около 15см', 'ficus_golden_monique.jpg', '', 'false'),
(19, 19, 'false', 'Форма роста - дерево/куст. Мелколистная форма фикуса. Листья около 2,5 см. окрашены в зелёный цвет с салатово-белыми мазками по краю. При сильном притенении теряет пестролистность.', 'укоренённый черенок около 15см', 'ficus_kinky.jpg', '', 'false'),
(20, 20, 'false', 'Форма роста - дерево/куст. Мелколистный сорт.Листья зелёные широкой белой окантовкой.  Темп роста средний', 'временно нет', 'ficus_nicole.jpg', '', 'true'),
(21, 21, 'false', 'Форма роста - дерево/куст.  Мелколистный сорт. Листовая пластина вытянутой формы,окрашена в зелёный цвет, по краю тонкая белая кайма.', 'временно нет', 'ficus_nina.jpg', '', 'true'),
(22, 22, 'false', 'Форма роста - дерево/куст. Листовая пластина среднего размера, овальная, окрашена в насыщенный зелёный цвет. По краю листа широкий ярко зелёный кант.', 'временно нет', 'ficus_regidan.jpg', '', 'true'),
(23, 23, 'false', 'Форма роста - дерево/куст. Листовая пластина меньше среднего. Окрашена я яркий салатовый цвет. По центральной жилке изумрудно-зелёные штрихи.', 'временно нет', 'ficus_reginald.jpg', '', 'true'),
(24, 24, 'false', 'Форма роста - дерево/куст. Листья около 5см. Поверхность листа покрыта частыми белыми мазками. Очень эффектный сорт.', 'временно нет', 'ficus_safari.jpg', '', 'true'),
(25, 25, 'false', 'Форма роста - дерево/куст.Очень яркий сорт. Листья около 6 см, имеют широкую яркую белую или кремовую полосу по краю. Темп роста выше среднего. Теневынослив', 'временно нет', 'ficus_starlight.jpg', '', 'true'),
(26, 26, 'true', 'новинка! Форма роста - дерево/куст. Листья около 5см, почти полностью цвета топлёного молока. По центру зелёная полоса. Темп роста ниже среднего. Светолюбив. Не выносит прямого солнечного света', 'временно нет', 'retusa_variegated.jpg', '', 'true'),
(27, 27, 'true', 'новинка! Листья практически треугольные, золотистые, кожистые.', 'укоренённый черенок около 10см', 'sp_t08_golden_var.jpg', '', 'false'),
(28, 28, 'true', 'новинка! Форма роста - дерево/куст. Листья треугольные, около 5 см, окрашены в кремово-зелёные тона. Темп роста ниже среднего. Светолюбив. Не выносит прямого солнечного света', 'временно нет', 'ficus_triangularis_var.jpg', '', 'true'),
(29, 29, 'true', 'новинка! Листья практически треугольные, кант белый.', 'укоренённый черенок около 10см', 'ficus_triangularis_white.jpg', '', 'false'),
(30, 30, 'true', 'новинка! Ворма роста куст/лиана. Темп роста средний. Листья глянцевые, не опушенные.По центру широкая светлая полоса. Очень декоративный сорт. Нуждается в притенении и опрыскиваниях', ' укоренённый черенок', 'maranta_bicolor.jpg', '', 'false'),
(31, 31, 'false', 'Ворма роста куст/лиана. Темп роста быстрый. Растение нуждается в опоре. Листья крупные, около 12-15см,бархатичтые, очень глубокого зелёного цвета с практически белой центральной жилкой. Нуждается в притенении и опрыскиваниях', ' укоренённый черенок', 'maranta_leuconeura.jpg', '', 'false'),
(32, 32, 'false', 'Ворма роста куст/лиана. Темп роста быстрый. Листья овальные, около 12см, окрашены в интенсивно зелёный цвет с контрастными красными прожилками. Нуждается в притенении и опрыскиваниях', ' укоренённый черенок', 'leuconeura_var_erythroneura.jpg', '', 'false'),
(33, 33, 'true', 'новинка! Ворма роста куст/лиана. Темп роста средний. Листья изумрудно-зелёные. Глянцевые, по центру широкая серебристо-белая полоса. Очень декоративный сорт. Нуждается в притенении и опрыскиваниях', 'временно нет', 'emerald_beauty.jpg', '', 'true'),
(34, 34, 'false', 'Ворма роста куст/лиана. Темп роста выше среднего. Листья около 12 см, бархатистые, тёмно-зелёные с практически чёрным центром и белой широкой полосой по центральной жилке. Нуждается в притенении и опрыскиваниях', 'временно нет', 'massangeana.jpg', '', 'true'),
(35, 35, 'true', 'новинка! Форма роста куст/лиана. Темп роста средний. Интереснгая пестролистная форма маранты. Листья овальные, около 10. Нуждается в притенении и опрыскиваниях', 'временно нет', 'beauty_kim.jpg', '', 'true'),
(36, 36, 'false', 'Форма роста лиана. Темп роста выше среднего. Листовые пластины ярко-зелёные, вытянутые. С возрастом размер листьев увеличивается, форма листьев несколько меняется. Растение выглядит ажурным, за счёт большого количества отверстий на поверхности листьев.', ' укоренённый черенок', 'monstera_adansonii.jpg', '', 'false'),
(37, 37, 'true', 'новинка!', ' укоренённый черенок', 'monstera_thai.jpg', '', 'false'),
(38, 38, 'false', 'Форма роста лиана. Темп роста выше среднего. Листья тёмно-зелёные, фактурые. Покрыты выпуклостями. Растение нуждается в опоре. Теневыносливый сорт. Не прихотливый в уходе.', ' укоренённый черенок', 'monstera_karstenianum.jpg', '', 'false'),
(39, 39, 'true', 'новинка!', ' укоренённый черенок', 'monstera_yellow.jpg', '', 'false'),
(40, 40, 'true', 'новинка! Форма роста лиана. Темп роста выше среднего. Листовые пластины около 10 см, месяцообразной формы, покрыты красивым серебряным узором. Предпочитает повышенную влажность воздуха и полив без пересушки. Отлично растёт на восточном окне. От прямого солна нужно притенять. Не прихотлива в уходе.', 'временно нет', 'monstera_siltepecana_el_salvadore.jpg', '', 'true'),
